import React from "react";
import '../App.css';
import $ from 'jquery';

export default function Home(){
    
    (function () {
        const second = 1000,
              minute = second * 60,
              hour = minute * 60,
              day = hour * 24;
      
        //I'm adding this section so I don't have to keep updating this pen every year :-)
        //remove this if you don't need it
        let today = new Date(),
            dd = String(today.getDate()).padStart(2, "0"),
            mm = String(today.getMonth() + 1).padStart(2, "0"),
            yyyy = today.getFullYear(),
            nextYear = yyyy + 1,
            dayMonth = "09/30/",
            birthday = dayMonth + yyyy;
        
        today = mm + "/" + dd + "/" + yyyy;
        if (today > birthday) {
          birthday = dayMonth + nextYear;
        }
        //end
        
        const countDown = new Date(birthday).getTime(),
            x = setInterval(function() {    
      
              const now = new Date().getTime(),
                    distance = countDown - now;
      
              document.getElementById("days").innerText = Math.floor(distance / (day)),
                document.getElementById("hours").innerText = Math.floor((distance % (day)) / (hour)),
                document.getElementById("minutes").innerText = Math.floor((distance % (hour)) / (minute)),
                document.getElementById("seconds").innerText = Math.floor((distance % (minute)) / second);
      
              //do something later when date is reached
              if (distance < 0) {
                document.getElementById("headline").innerText = "It's my birthday!";
                document.getElementById("countdown").style.display = "none";
                document.getElementById("content").style.display = "block";
                clearInterval(x);
              }
              //seconds
            }, 0)
        }());

return (

    
    <div>
        <h1>Slide Down Effect (Style 4)</h1>
	<nav class="style-4">
		<ul class="menu-4">
		  <li class="current"><a href="#" data-hover="Home">Home</a></li>
		  <li><a href="#" data-hover="About Us">About Us</a></li>
		  <li><a href="#" data-hover="Blog">Blog</a></li>
		  <li><a href="#" data-hover="Services">Services</a></li>
		  <li><a href="#" data-hover="Products">Products</a></li>
		  <li><a href="#" data-hover="Contact">Contact</a></li>
		</ul>
	</nav>
    <div class="container">
    <h1 style={{color:'white', fontFamily:'Arial'}}>HOLA MOZILLA 2.0</h1>
    <div style={{color:'white', fontFamily:'Arial'}}><span>21</span> <span>st</span> <span>December at 7.00 pm</span></div>
   
   
    
  <h1 id="headline">Countdown to my birthday</h1>
  <div id="countdown">
    <ul>
      <li><span id="days"></span>days</li>
      <li><span id="hours"></span>Hours</li>
      <li><span id="minutes"></span>Minutes</li>
      <li><span id="seconds"></span>Seconds</li>
    </ul>
  </div>
</div>
</div>
)
}
