import React, { useEffect, useRef } from "react";
import '../App.css';
import $ from 'jquery';
import { Button } from "@material-ui/core";

export default function YourTicket(){

     const canvasRef = useRef(null);

  useEffect(()=> {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      let imgUrl = "https://res.cloudinary.com/fitness-glory/image/upload/v1639215281/aie_li5drw.jpg"
      const nameInput = document.getElementById('name')
      const downloadBtn = document.getElementById('download-btn')
      const image = new Image();
      image.crossOrigin = "Anonymous";
      image.src = imgUrl;
      image.onload = function () {
        drawImage()
      }
      function drawImage() {
        ctx.drawImage(image, 0, 0, canvas.width, canvas.height)
        ctx.font = '40px Montserrat'
        ctx.fillStyle = '#29e'
        ctx.fillText(nameInput.value, 70, 290)
      }
      nameInput.addEventListener('input', function () {
        drawImage()
      });
      
      downloadBtn.addEventListener('click', function () {
        downloadBtn.href = canvas.toDataURL('image/jpg')
        downloadBtn.download = 'MozillaTicket - ' + nameInput.value
      });
      image.onerror = function(err) {
          console.log("err", err);
      };
      
  });

    return(
<div>
     <>
     <div className="ticket-left">
         <h2>Generate your Ticket <br/><span>FROM HERE!</span></h2>
         <span>
            Enter your Name:
            <input id="name" type='text' required/>
        </span>
     </div>
      
        
            <canvas ref={canvasRef} height={500} width={350} style={{border: '1px solid black', position: 'center'}}/>
            <a href="#" id="download-btn">Generate Tag</a>
        </>
</div>
    )
}
