import React from "react";
import '../App.css';
import $ from 'jquery';

export default function Home(){
   
// Set the date we're counting down to

var countDownDate = new Date("Feb 15, 2022 0:0:25").getTime();




// Update the count down every 1 second

var x = setInterval(function() {




  // Get today's date and time

  var now = new Date().getTime();




  // Find the distance between now and the count down date

  var distance = countDownDate - now;




  // Time calculations for days, hours, minutes and seconds

  var days = Math.floor(distance / (1000 * 60 * 60 * 24));

  var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));

  var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));

  var seconds = Math.floor((distance % (1000 * 60)) / 1000);




  // Display the result in the element with id="countdown". The portions of the strings within "" can be edited. 

  // For example, "d" can be changed to "Days ", "h " can be changed to "Hours ".. and so forth


 document.getElementById("countdown").innerHTML = days + "d " + hours + "h " + minutes + "m " + seconds + "s ";




  // If the count down is finished, write some text. The string can be adjusted for your Page

  if (distance < 0) {

    clearInterval(x);

   document.getElementById("countdown").innerHTML = "THIS DEAL HAS EXPIRED";

 }

}, 1000);

(function () {
    const second = 1000,
          minute = second * 60,
          hour = minute * 60,
          day = hour * 24;
  
    //I'm adding this section so I don't have to keep updating this pen every year :-)
    //remove this if you don't need it
    let today = new Date(),
        dd = String(today.getDate()).padStart(2, "0"),
        mm = String(today.getMonth() + 1).padStart(2, "0"),
        yyyy = today.getFullYear(),
        nextYear = yyyy + 1,
        dayMonth = "09/30/",
        birthday = dayMonth + yyyy;
    
    today = mm + "/" + dd + "/" + yyyy;
    if (today > birthday) {
      birthday = dayMonth + nextYear;
    }
    //end
    
    const countDown = new Date(birthday).getTime(),
        x = setInterval(function() {    
  
          const now = new Date().getTime(),
                distance = countDown - now;
  
          document.getElementById("days").innerText = Math.floor(distance / (day)),
            document.getElementById("hours").innerText = Math.floor((distance % (day)) / (hour)),
            document.getElementById("minutes").innerText = Math.floor((distance % (hour)) / (minute)),
            document.getElementById("seconds").innerText = Math.floor((distance % (minute)) / second);
  
          //do something later when date is reached
          if (distance < 0) {
            document.getElementById("headline").innerText = "It's my birthday!";
            document.getElementById("countdown").style.display = "none";
            document.getElementById("content").style.display = "block";
            clearInterval(x);
          }
          //seconds
        }, 0)
    }());

return (
    <div>
    <h1 style={{color:'white', fontFamily:'Arial'}}>HOLA MOZILLA 2.0</h1>
    <div style={{color:'white', fontFamily:'Arial'}}><span>21</span> <span>st</span> <span>December at 7.00 pm</span></div>
    <p style={{color:'white', fontFamily:'Arial'}} id="countdown"></p>
    </div>
)
}
