import React from "react";
import {
  AppBar,
  Toolbar,
  CssBaseline,
  Typography,
  makeStyles,
  useTheme,
  useMediaQuery,
} from "@material-ui/core";
import { Link } from "react-router-dom";
import DrawerComponent from "./Drawer";

const useStyles = makeStyles((theme) => ({
  navlinks: {
    marginLeft: theme.spacing(2),
    display: "flex",
  },
  logo: {
    flexGrow: "1",
    cursor: "pointer",
  },
  link: {
    textDecoration: "none",
    color: "white",
    fontSize: "20px",
    marginLeft: theme.spacing(20),
    "&:hover": {
      color: "yellow",
      borderBottom: "1px solid white",
    },
  },
}));

function Navbar() {
  const classes = useStyles();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("md"));
  const [currentRouterPathName, setCurrentRouterPathName] = React.useState("");
  const router = useRouter();
  useEffect(() => {
    // console.log(router.pathname);
    setCurrentRouterPathName(router.pathname);
    return () => { };
  }, [router.pathname]);

  return (
    <AppBar position="static" style={{backgroundColor:"black"}}>
      <CssBaseline />
      <Toolbar>
        <Typography variant="h6" className={classes.logo}>
          <img src="https://res.cloudinary.com/fitness-glory/image/upload/v1639477576/Logo-02_xgoth5.png"
           alt="logo"
           width={200}
           height={100}
          />
          
        </Typography>
        {isMobile ? (
          <DrawerComponent />
        ) : (
          <div className={classes.navlinks}>
           <Link href="/" passHref={true}>
                    <Button className={
                      currentRouterPathName === "/" ? "active" : ""
                    }
                            color="inherit"
                    >
                      <span> HOME </span>
                    </Button>
                  </Link>

                  <Link href="/redeem-asset" passHref={true}>
                    <Button className={
                      currentRouterPathName === "/redeem-asset" ? "active" : ""
                    }
                            color="inherit"
                    >
                      <span> ABOUT </span>
                    </Button>
                  </Link>

                  <Link href="/redeem-asset" passHref={true}>
                    <Button className={
                      currentRouterPathName === "/redeem-asset" ? "active" : ""
                    }
                            color="inherit"
                    >
                      <span> REGISTER </span>
                    </Button>
                  </Link>

                  <Link href="/redeem-asset" passHref={true}>
                    <Button className={
                      currentRouterPathName === "/redeem-asset" ? "active" : ""
                    }
                            color="inherit"
                    >
                      <span> YOUR TICKET </span>
                    </Button>
                  </Link>

                  <Link href="/redeem-asset" passHref={true}>
                    <Button className={
                      currentRouterPathName === "/redeem-asset" ? "active" : ""
                    }
                            color="inherit"
                    >
                      <span> CONTACT </span>
                    </Button>
                  </Link>
          </div>
        )}
      </Toolbar>
    </AppBar>
  );
}
export default Navbar;
