import aie from './aie.jpg';
import './App.css';

function App() {

  const canvas = document.getElementById('canvas')
const ctx = canvas.getContext('2d')
const nameInput = document.getElementById('name');
const downloadBtn = document.getElementById('download-btn');

const image = new Image();
image.src = 'aie.jpg'
image.onload = function () {
	drawImage()
}

function drawImage() {
	// ctx.clearRect(0, 0, canvas.width, canvas.height)
	ctx.drawImage(image, 0, 0, canvas.width, canvas.height)
	ctx.font = '40px monotype corsiva'
	ctx.fillStyle = '#29e'
	ctx.fillText(nameInput.value, 70, 290)
}

nameInput.addEventListener('input', function () {
	drawImage();
});

downloadBtn.addEventListener('click', function () {
	downloadBtn.href = canvas.toDataURL('image/jpg')
	downloadBtn.download = 'Certificate - ' + nameInput.value
});

  return (
    <div className="App">
      <div class="container">
        <h1>Pasinduge Mozilla Labbe Tag generator eka</h1>
        <h2>
            Enter your Name:
            <input id="name" type='text'/>
        </h2>
        <a href="#" id="download-btn">Generate Tag</a>
        <canvas id="canvas" height="500px" width="350px"></canvas>
    </div>
    </div>
  );
}

export default App;
