import React, { useEffect, useRef } from "react";
import '../App.css';
import $ from 'jquery';
import { Button } from "@material-ui/core";
import TextField from '@material-ui/core/TextField';
import { makeStyles } from "@material-ui/core/styles";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";

const useStyles = makeStyles(theme => ({
    root: {
      display: "flex",
      flexWrap: "wrap",
    },
    formControl: {
      margin: theme.spacing(1),
      minWidth: 200
    },
    selectEmpty: {
      marginTop: theme.spacing(2)
    }
  }));
  const useOutlinedInputStyles = makeStyles(theme => ({
    root: {
      "& $notchedOutline": {
        borderColor: "red"
      },
      "&:hover $notchedOutline": {
        borderColor: "blue"
      },
      "&$focused $notchedOutline": {
        borderColor: "green"
      }
    },
    focused: {},
    notchedOutline: {}
  }));
  
export default function YourTicket(){
    const classes = useStyles();
    const outlinedInputClasses = useOutlinedInputStyles();
     const canvasRef = useRef(null);

     const inputLabel = React.useRef(null);
  const [labelWidth, setLabelWidth] = React.useState(0);
  React.useEffect(() => {
    setLabelWidth(inputLabel.current.offsetWidth);
  }, []);
  useEffect(()=> {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      let imgUrl = "https://res.cloudinary.com/fitness-glory/image/upload/v1639504502/white_ticket_mjyc78.png"
      const nameInput = document.getElementById('name')
      const downloadBtn = document.getElementById('download-btn')
      const image = new Image();
      image.crossOrigin = "Anonymous";
      image.src = imgUrl;
      image.onload = function () {
        drawImage()
      }
      function drawImage() {
        ctx.drawImage(image, 0, 0, canvas.width, canvas.height)
        ctx.font = '40px Montserrat'
        ctx.fillStyle = '#f83600'
        ctx.fillText(nameInput.value, 45, 130)
      }
      nameInput.addEventListener('input', function () {
        drawImage()
      });
      
      downloadBtn.addEventListener('click', function () {
        downloadBtn.href = canvas.toDataURL('image/jpg')
        downloadBtn.download = 'MozillaTicket - ' + nameInput.value
      });
      image.onerror = function(err) {
          console.log("err", err);
      };
      
  });

    return(
<div>
     <>
     <div className="ticket-left">
         <span>Generate your Ticket <br/><span>FROM HERE!</span></span>
         <div>
            {/* <input className="nameInput" id="name" type='text' placeholder="Enter Your Name"/> */}
            <form className={classes.root} autoComplete="off">
            <FormControl variant="outlined"  className={classes.formControl}>
                                                    <InputLabel style={{color:"white"}} ref={inputLabel} htmlFor="outlined-age-simple">Enter Your Name</InputLabel>
                                                    <OutlinedInput
                                                    id="name"
                                                        type={'text'}
                                                        style={{color:"white"}}
                                                        labelWidth={labelWidth}
              
              
              classes={outlinedInputClasses}
                                                    />
                                                </FormControl></form>
        </div>
     </div>
      
        <div className="ticket-right">
            <canvas ref={canvasRef} height="440px" width="840px" style={{border: '1px solid black', position: 'center'}}/>
            </div>
            <a href="#" style={{ textDecoration: "none" }} id="download-btn">Generate Tag</a>
        </>
        
</div>
    )
}
