import React from "react";
import "../App.css";
import $ from "jquery";
import { Button } from "@material-ui/core";

export default function Home() {
  (function () {
    const second = 1000,
      minute = second * 60,
      hour = minute * 60,
      day = hour * 24;

    //I'm adding this section so I don't have to keep updating this pen every year :-)
    //remove this if you don't need it
    // let today = new Date(),
    //     dd = String(today.getDate()).padStart(2, "0"),
    //     mm = String(today.getMonth() + 1).padStart(2, "0"),
    //     yyyy = today.getFullYear(),
    //     nextYear = yyyy + 1,
    //     dayMonth = "09/30/",
    //     birthday = dayMonth + yyyy;

    // today = mm + "/" + dd + "/" + yyyy;
    // if (today > birthday) {
    //   birthday = dayMonth + nextYear;
    // }
    //end

    const countDown = new Date("Dec 21, 2021 19:37:25").getTime(),
      x = setInterval(function () {
        const now = new Date().getTime(),
          distance = countDown - now;
        if (typeof days !== "undefined" && days !== null) {
          (document.getElementById("days").innerText = Math.floor(
            distance / day
          )),
            (document.getElementById("hours").innerText = Math.floor(
              (distance % day) / hour
            )),
            (document.getElementById("minutes").innerText = Math.floor(
              (distance % hour) / minute
            )),
            (document.getElementById("seconds").innerText = Math.floor(
              (distance % minute) / second
            ));
        }
        //do something later when date is reached
        if (distance < 0) {
          document.getElementById("countdown").style.display = ":";
          document.getElementById("content").style.display = "block";
          clearInterval(x);
        }
        //seconds
      }, 0);
  })();

  return (
    <div className="main">
      <center>
        <div class="container">
          <h1 className="mainHeading">HOLA MOZILLA 2.0</h1>
          <div className="dateText">
            <span>21</span>
            <span>st</span> <span>December @ 7.00pm</span>
          </div>

          <div id="countdown" className="countdown">
            <ul>
              <li>
                <span class="font-effect-fire" id="days"></span>
                <tx style={{ color: "#f83600" }}>days</tx>
              </li>
              <li>
                <span class="font-effect-fire" id="hours"></span>
                <tx style={{ color: "#f83600" }}>Hours</tx>
              </li>
              <li>
                <span class="font-effect-fire" id="minutes"></span>
                <tx style={{ color: "#f83600" }}>Minutes</tx>
              </li>
              <li>
                <span class="font-effect-fire" id="seconds"></span>
                <tx style={{ color: "#f83600" }}>Seconds</tx>
              </li>
            </ul>
          </div>

          <img
            className="watermark"
            src="https://res.cloudinary.com/fitness-glory/image/upload/v1639484985/logo_icon-01_s6oex8.png"
          />

          <Button className="myBtn">
            <a style={{ textDecoration: "none" }}>Register Now</a>
          </Button>
        </div>
      </center>
    </div>
  );
}
